import React from "react";
import {ContactPicker} from "../contactPicker/ContactPicker";

const getTodayString = () => {
  const [month, day, year] = new Date()
    .toLocaleDateString("en-US")
    .split("/");
  return `${year}-${month.padStart(2, "0")}-${day.padStart(
    2,
    "0"
  )}`;
};

export const AppointmentForm = ({
  contacts,
  title,
  setTitle,
  contact,
  setContact,
  date,
  setDate,
  time,
  setTime,
  handleSubmit,
}) => {

  

  return (
    <>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Event Title</label>
          <input type="text" name="name" id="name" value={title} onChange={setTitle} />
        </div>
        <div>
          <label htmlFor="date">Date</label>
          <input type="date" name="date" id="date" min={getTodayString()} value={date} onChange={setDate} />
        </div>
        <div>
          <label htmlFor="time">Time</label>
          <input type="time" name="time" id="time" value={time} onChange={setTime} />
        </div>
        <div>
        <label htmlFor="contact">Contact</label>
        <ContactPicker contacts={contacts} onChange={setContact} value={contact} name="contact" id="contact" />
        </div>
        <button type="submit">Submit</button>
      </form>
    </>
  );
};
