import React, { useState, useEffect } from "react";

import { ContactForm } from "../../components/contactForm/ContactForm";
import { TileList } from "../../components/tileList/TileList";

export const ContactsPage = ({contacts, handleNewContact}) => {
  

  const [currentName, setCurrentName] = useState("");
  const [currentPhone, setCurrentPhone] = useState("");
  const [currentEmail, setCurrentEmail] = useState("");
  const [duplicate, setDuplicate] = useState(false);


  const handleSubmit = (e) => {
    e.preventDefault();
    const data = e.target.elements;
    if(!duplicate){
    const newName = e.target.name;
    const newPhone = e.target.phone;
    const newEmail = e.target.email;
    handleNewContact(newName, newPhone, newEmail);
    setCurrentName("");
    setCurrentPhone("");
    setCurrentEmail("");
  }
  };

  useEffect(() => {
    if(contacts.includes(currentName)){
      setDuplicate(true);
    } else {
      setDuplicate(false);
    }
  }, [contacts, currentName])

  return (
    <div>
      <section>
        <h2>Add Contact</h2> 
      </section>
      <hr />
      <section>
        <h2>Contacts</h2>
      </section>
    </div>
  );
};
