import { keyboard } from "@testing-library/user-event/dist/keyboard";
import React from "react";

export const Tile = ({name, description}) => {

  const descArray = description.values();
  const descElements = [];
  descArray.forEach((index, key, value) => {
    descElements.push(<p key={index}>{key}: {value}</p>)
  })
  
  return (
    <div className="tile-container">
      <p>{name}</p>
      {descElements}
    </div>
  );
};
